#![feature(iter_next_chunk)]

use part_one::process_one;
use part_two::process_two;

mod part_one;
mod part_two;

#[test]
fn part_one_test() {
    assert_eq!(process_one("bvwbjplbgvbhsrlpgdmjqwftvncz"), 5);
    assert_eq!(process_one("nppdvjthqldpwncqszvftbrmjlhg"), 6);
    assert_eq!(process_one("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"), 10);
    assert_eq!(process_one("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"), 11);
}

#[test]
fn part_one() {
    let result = process_one("");
    dbg!(result);
}

#[test]
fn part_two_test() {
    assert_eq!(process_two("mjqjpqmgbljsphdztnvjfqwrcgsmlb"), 19);
    assert_eq!(process_two("bvwbjplbgvbhsrlpgdmjqwftvncz"), 23);
    assert_eq!(process_two("nppdvjthqldpwncqszvftbrmjlhg"), 23);
    assert_eq!(process_two("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"), 29);
    assert_eq!(process_two("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"), 26);
}

#[test]
fn part_two() {
    let result = process_two("");
    dbg!(result);
}
